const pageVariants = {
    initial: {
        opacity: 0,
        x: "+100vw",
        scale: 0.8
    },
    in: {
        opacity: 1,
        x: 0,
        scale: 1
    },
    out: {
        opacity: 0,
        x: "-100vw",
        scale: 0.8
    },
};

const pageTransition = {
    type: "tween",
    ease: "anticipate",
    duration: "1"
}

module.exports = {pageVariants, pageTransition};
